object SettingsPrefrences: KotprefModel() {

    const val INDONESIA = 0
    const val ENGLISH = 1

    const val LIGHT_MODE = false

    const val FOCUS_READ = false

    var isOnBoarding by booleanPref(
        true
    )

    var selectedLanguage by intPref(
        INDONESIA
    )

    var isDarkMode by booleanPref(
        LIGHT_MODE
    )

    var isVisibleTranslate by booleanPref(
        FOCUS_READ
    )

    var selectedQori by enumOrdinalPref(
        Qaries.AL_AFASY
    )
}

